import { createStore } from 'vuex'

export default createStore({
  state: {
    pedido: [] 
  },
  getters: {
  },
  mutations: {
    guardarPedido(state,pedido){
      state.pedido.push(pedido) ;
      console.log(state.pedido);

    },
    algo(esto){
      alert(esto)
    }
  },
  actions: {
  },
  modules: {
  }
})
