import { createStore } from 'vuex'

export default createStore({
  state: {
    pedidos: [], 
    armar: false
  },
  getters: {
    pedidos(state){
      return state.pedidos;
    },
    armar(state){
      return state.armar
    }
  },
  mutations: {
    guardarPedido(state,pedido){
      state.pedidos.push(pedido) ;
      console.log(state.pedidos);

    },guardarEstatus(state,armar){
      state.armar =armar;
    }
