import { createStore } from 'vuex'

export default createStore({
  state: {
    pedidos: [], 
    mk: 10,
    moka:10,
    nc:10,
    tc:10,
    zananoria:10,
    vainilla:10,
    aleman:10,
    cajeta:10,
    cc:10,
    durazno:10

  },
  getters: {
    pedidos(state){
      return state.pedidos;
    },
    armar(state){
      return state.armar
    }
  },
  mutations: {
    guardarPedido(state,pedido){
      state.pedidos.push(pedido) ;
    }
  },
  actions: {
  },
  modules: {
  }
})
