import { createStore } from 'vuex'

export default createStore({
  state: {
    pedido: {} 
  },
  getters: {
  },
  mutations: {
    guardarPedido(state,pedido){
      state.pedio = pedido;
      console.log(pedido);

    },
    algo(esto){
      alert(esto)
    }
  },
  actions: {
  },
  modules: {
  }
})
